frappe.ui.form.on('Payment Entry', {
    onload: function(frm) {
        frm.add_custom_button(__('Purchase Order'), function() {
            var d = new frappe.ui.form.MultiSelectDialog({
                doctype: "Purchase Order",
                target: frm,
                setters: {
                    supplier_name: null,
                    schedule_date: null
                },
                action(selections, args) {
                    // Extract the selected Purchase Order names
                    var selectedPurchaseOrders = selections.map(item => `${item.name} (Supplier: ${item.supplier_name})`);
                    console.log(selectedPurchaseOrders);
                    frappe.msgprint("Selected Purchase Orders: " + selectedPurchaseOrders.join(', '));
                    // Make an HTTP request to the server script
                    frappe.call({
                        method: 'API',
                        args: {
                            selected_purchase_orders: selections
                        },
                        callback: function(response) {
                            if (!response.exc) {
                                // Successfully received a response from the server
                                frappe.msgprint(response.message);
                            }
                        }
                    });
                }
            });
            d.show();
        }, __("Get Items From"));
    }
});

frappe.ui.form.on('Supplier', {
    refresh(frm) {
        // Access the value of the "pan" field
        var panValue = frm.doc.pan;

        // Check if the pan number has at least 4 characters and the fourth character is 'p'
        if (panValue && panValue.length >= 4 && panValue[3].toLowerCase() === 'p') {
            // Set the value of the "custom_sub_category_" field to "Propritorship"
            frappe.model.set_value(frm.doctype, frm.docname, 'custom_sub_category_', 'Propritorship');

            // Set the value of the "custom_company_status" field to "2"
            frappe.model.set_value(frm.doctype, frm.docname, 'custom_company_status', '2');
        } else {
            // If the condition is not met, set the value to "Other" for "custom_sub_category_"
            frappe.model.set_value(frm.doctype, frm.docname, 'custom_sub_category_', 'Other');

            // Set the value of the "custom_company_status" field to a default value (e.g., "1")
            frappe.model.set_value(frm.doctype, frm.docname, 'custom_company_status', '1');
        }

        // Print the value using frappe.msgprint
        // frappe.msgprint("Pan Value: " + panValue);
    }
});

// Set custom_pan_value to 'Yes' if pan has a value
frappe.ui.form.on('Supplier', {
    validate(frm) {
        if (frm.doc.pan) {
            // Set the value of the "custom_pan_value" field to "Yes"
            frappe.model.set_value(frm.doctype, frm.docname, 'custom_pan_available', 'Yes');
        }
    }
});
