script:
frappe.ui.form.on('Payment Entry', {
    onload: function(frm) {
        frm.add_custom_button(__('Purchase Order'), function() {
            var d = new frappe.ui.form.MultiSelectDialog({
                doctype: "Purchase Order",
                target: frm,
                setters: {
                    supplier_name: null,
                    schedule_date: null
                },
            });
            d.show();
        }, __("Get Items From"));
        frm.add_custom_button(__('Purchase Invoice'), function() {
            var d = new frappe.ui.form.MultiSelectDialog({
                doctype: "Purchase Invoice",
                target: frm,
                setters: {
                    supplier_name: null,
                    posting_date:null

                },
            });
            d.show();
        }, __("Get Items From"));

        frm.add_custom_button(__('Sales Order'), function() {
            var d = new frappe.ui.form.MultiSelectDialog({
                doctype: "Sales Order",
                target: frm,
                setters: {
                    customer: null,
                    delivery_date: null

                },
            });
            d.show();
        }, __("Get Items From"));

        frm.add_custom_button(__('Sales Invoice'), function() {
            var d = new frappe.ui.form.MultiSelectDialog({
                doctype: "Sales Invoice",
                target: frm,
                setters: {
                    customer: null,
                     posting_date:null

                },
            });
            d.show();
        }, __("Get Items From"));
    }
});
